mapper.update:
update t_user 
set room_card_num = room_card_num - #{deductNum}, update_time = now() 
where player_id = #{playerId} and room_card_num = #{roomCardNum} and update_time = #{updateTime}

update t_user 
set room_card_num = room_card_num + #{addNum}, update_time = now() 
where player_id = #{playerId}
